import PDFDocument from "pdfkit";
import type { Response } from "express";
import type { Property, Lead } from "@shared/schema";

export class PDFGenerator {
  // Generate property brochure PDF
  static async generatePropertyBrochure(property: Property, res: Response) {
    const doc = new PDFDocument({ margin: 50 });

    // Set response headers
    res.setHeader("Content-Type", "application/pdf");
    res.setHeader("Content-Disposition", `attachment; filename=property-${property.id}.pdf`);

    // Pipe PDF to response
    doc.pipe(res);

    // Add header
    doc.fontSize(24)
       .fillColor("#1e40af")
       .text("Sangli Properties LLP", { align: "center" })
       .moveDown(0.5);

    doc.fontSize(20)
       .fillColor("#000000")
       .text(property.title || "Property Details", { align: "center" })
       .moveDown(1);

    // Add property details
    doc.fontSize(14).fillColor("#6b7280").text("Property Information", { underline: true });
    doc.moveDown(0.5);

    doc.fontSize(12).fillColor("#000000");

    if (property.type) {
      doc.text(`Type: ${property.type}`);
    }
    if (property.location) {
      doc.text(`Location: ${property.location}`);
    }
    if (property.price) {
      doc.text(`Price: â‚¹${parseFloat(property.price).toLocaleString('en-IN')}`);
    }
    if (property.area) {
      doc.text(`Area: ${property.area.toLocaleString()} sqft`);
    }
    if (property.status) {
      doc.text(`Status: ${property.status}`);
    }

    doc.moveDown(1);

    // Add description if available
    if (property.description) {
      doc.fontSize(14).fillColor("#6b7280").text("Description", { underline: true });
      doc.moveDown(0.5);
      doc.fontSize(12).fillColor("#000000").text(property.description, {
        align: "justify",
        width: 500,
      });
      doc.moveDown(1);
    }

    // Add footer
    doc.moveDown(2);
    doc.fontSize(10)
       .fillColor("#9ca3af")
       .text("Generated by Sangli Properties CRM", { align: "center" });

    doc.fontSize(10)
       .text(`Date: ${new Date().toLocaleDateString()}`, { align: "center" });

    // Finalize PDF
    doc.end();
  }

  // Generate leads report PDF
  static async generateLeadsReport(leads: Lead[], res: Response) {
    const doc = new PDFDocument({ margin: 50 });

    // Set response headers
    res.setHeader("Content-Type", "application/pdf");
    res.setHeader("Content-Disposition", `attachment; filename=leads-report-${Date.now()}.pdf`);

    // Pipe PDF to response
    doc.pipe(res);

    // Add header
    doc.fontSize(24)
       .fillColor("#1e40af")
       .text("Sangli Properties LLP", { align: "center" })
       .moveDown(0.5);

    doc.fontSize(18)
       .fillColor("#000000")
       .text("Leads Report", { align: "center" })
       .moveDown(1);

    // Add summary
    doc.fontSize(12).fillColor("#6b7280");
    doc.text(`Total Leads: ${leads.length}`);
    doc.text(`Report Generated: ${new Date().toLocaleDateString()}`);
    doc.moveDown(1);

    // Add leads table
    doc.fontSize(14).fillColor("#6b7280").text("Lead Details", { underline: true });
    doc.moveDown(0.5);

    leads.forEach((lead, index) => {
      if (index > 0) {
        doc.moveDown(0.5);
        doc.strokeColor("#e5e7eb").moveTo(50, doc.y).lineTo(550, doc.y).stroke();
        doc.moveDown(0.5);
      }

      doc.fontSize(12).fillColor("#000000");
      doc.text(`Name: ${lead.name}`);
      doc.text(`Phone: ${lead.phone}`);
      if (lead.email) doc.text(`Email: ${lead.email}`);
      doc.text(`Source: ${lead.source}`);
      doc.text(`Stage: ${lead.stage}`);
      if (lead.budget) {
        doc.text(`Budget: â‚¹${parseFloat(lead.budget).toLocaleString('en-IN')}`);
      }
      if (lead.preferredLocation) {
        doc.text(`Preferred Location: ${lead.preferredLocation}`);
      }

      // Check if we need a new page
      if (doc.y > 700) {
        doc.addPage();
      }
    });

    // Add footer
    doc.fontSize(10)
       .fillColor("#9ca3af")
       .text("Generated by Sangli Properties CRM", { align: "center" });

    // Finalize PDF
    doc.end();
  }

  // Generate sales summary PDF
  static async generateSalesSummary(data: any, res: Response) {
    const doc = new PDFDocument({ margin: 50 });

    // Set response headers
    res.setHeader("Content-Type", "application/pdf");
    res.setHeader("Content-Disposition", `attachment; filename=sales-summary-${Date.now()}.pdf`);

    // Pipe PDF to response
    doc.pipe(res);

    // Add header
    doc.fontSize(24)
       .fillColor("#1e40af")
       .text("Sangli Properties LLP", { align: "center" })
       .moveDown(0.5);

    doc.fontSize(18)
       .fillColor("#000000")
       .text("Sales Summary Report", { align: "center" })
       .moveDown(1);

    // Add summary statistics
    doc.fontSize(14).fillColor("#6b7280").text("Summary", { underline: true });
    doc.moveDown(0.5);

    doc.fontSize(12).fillColor("#000000");
    doc.text(`Total Leads: ${data.totalLeads || 0}`);
    doc.text(`Active Leads: ${data.activeLeads || 0}`);
    doc.text(`Closed Deals: ${data.closedDeals || 0}`);
    doc.text(`Total Properties: ${data.totalProperties || 0}`);
    doc.text(`Available Properties: ${data.availableProperties || 0}`);
    doc.moveDown(1);

    // Add monthly sales data if available
    if (data.monthlySales && data.monthlySales.length > 0) {
      doc.fontSize(14).fillColor("#6b7280").text("Monthly Sales", { underline: true });
      doc.moveDown(0.5);

      data.monthlySales.forEach((item: any) => {
        doc.fontSize(12).fillColor("#000000");
        doc.text(`${item.month}: ${item.count} deals`);
      });
      doc.moveDown(1);
    }

    // Add top agents if available
    if (data.topAgents && data.topAgents.length > 0) {
      doc.fontSize(14).fillColor("#6b7280").text("Top Performing Agents", { underline: true });
      doc.moveDown(0.5);

      data.topAgents.forEach((agent: any, index: number) => {
        doc.fontSize(12).fillColor("#000000");
        doc.text(`${index + 1}. ${agent.name}: ${agent.closedDeals} closed deals`);
      });
      doc.moveDown(1);
    }

    // Add footer
    doc.fontSize(10)
       .fillColor("#9ca3af")
       .text(`Report Generated: ${new Date().toLocaleDateString()}`, { align: "center" })
       .moveDown(0.5);

    doc.fontSize(10)
       .text("Generated by Sangli Properties CRM", { align: "center" });

    // Finalize PDF
    doc.end();
  }
}

