                              data-testid={`owner-link-table-${property.id}`}
                            >
                              <User className="w-3 h-3" />
                              {(property as any).ownerName}
                            </span>
                          </Link>
                        ) : (
                          <span className="text-muted-foreground text-sm">-</span>
                        )}
                      </TableCell>
                      <TableCell>
                        <Badge variant="outline">{property.type}</Badge>
                      </TableCell>
                      <TableCell>
                        {property.transactionType ? (
                          <Badge variant="secondary">{property.transactionType}</Badge>
                        ) : (
                          <span className="text-muted-foreground text-sm">-</span>
                        )}
                      </TableCell>
                      <TableCell>{property.area} sqft</TableCell>
                      <TableCell className="font-semibold">â‚¹{Number(property.price).toLocaleString()}</TableCell>
                      <TableCell>
                        <Badge className={statusColors[property.status]}>{property.status}</Badge>
                      </TableCell>
                      <TableCell className="text-right">
                        <div className="flex justify-end gap-2">
                          <Button
                            size="icon"
                            variant="ghost"
                            onClick={(e) => {
                              e.stopPropagation();
                              handleEdit(property);
                            }}
                            data-testid={`button-edit-${property.id}`}
                          >
                            <Edit className="w-4 h-4" />
                          </Button>
                          <Button
