import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { setupAuth, isAuthenticated } from "./replitAuth";
import { insertLeadSchema, insertPropertySchema, insertOwnerSchema, insertClientSchema, insertActivitySchema, insertContactSubmissionSchema, insertDocumentAttachmentSchema } from "@shared/schema";
import { ObjectStorageService } from "./objectStorage";
import { PDFGenerator } from "./pdfGenerator";
// Facebook/Instagram integration - commented out for future use
// import { verifyFacebookWebhook, processFacebookWebhook } from "./facebookWebhook";

export async function registerRoutes(app: Express): Promise<Server> {
  // Auth middleware
  await setupAuth(app);

  // Auth routes
  app.get('/api/auth/user', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);
      res.json(user);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // User routes - Get all assignable users (Sales Agents and Marketing Executives)
  app.get('/api/users/agents', isAuthenticated, async (req, res) => {
    try {
      const allUsers = await storage.getAllUsers();
      const assignableUsers = allUsers.filter(u => 
        (u.role === "Sales Agent" || u.role === "Marketing Executive") && u.isActive === 1
      );
      res.json(assignableUsers);
    } catch (error) {
      console.error("Error fetching agents:", error);
      res.status(500).json({ message: "Failed to fetch agents" });
    }
  });

  // User Management (Admin only)
  app.get('/api/users', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (user?.role !== 'Admin') {
        return res.status(403).json({ message: "Admin access required" });
      }
      const users = await storage.getAllUsers();
      res.json(users);
    } catch (error) {
      console.error("Error fetching users:", error);
      res.status(500).json({ message: "Failed to fetch users" });
    }
  });

  app.post('/api/users', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (user?.role !== 'Admin') {
        return res.status(403).json({ message: "Admin access required" });
      }
      const { email, role } = req.body;

      if (!email || !role) {
        return res.status(400).json({ message: "Email and role are required" });
      }

      const validRoles = ["Admin", "Sales Agent", "Marketing Executive", "Property Manager"];
      if (!validRoles.includes(role)) {
        return res.status(400).json({ message: "Invalid role" });
      }

      // Check if user already exists
      const existingUsers = await storage.getAllUsers();
      if (existingUsers.find(u => u.email === email)) {
        return res.status(400).json({ message: "User with this email already exists" });
      }

      const newUser = await storage.createManualUser(email, role);
      res.json(newUser);
    } catch (error) {
      console.error("Error creating user:", error);
      res.status(500).json({ message: "Failed to create user" });
    }
  });

  app.patch('/api/users/:id/role', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (user?.role !== 'Admin') {
        return res.status(403).json({ message: "Admin access required" });
      }
      const { role } = req.body;
      const validRoles = ["Admin", "Sales Agent", "Marketing Executive", "Property Manager"];
      if (!validRoles.includes(role)) {
        return res.status(400).json({ message: "Invalid role" });
      }
      const updatedUser = await storage.updateUserRole(req.params.id, role);
      res.json(updatedUser);
    } catch (error) {
      console.error("Error updating user role:", error);
      res.status(500).json({ message: "Failed to update user role" });
    }
  });

  app.patch('/api/users/:id/deactivate', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (user?.role !== 'Admin') {
        return res.status(403).json({ message: "Admin access required" });
      }

      // Prevent deactivating self
      if (req.params.id === req.user.claims.sub) {
        return res.status(400).json({ message: "Cannot deactivate your own account" });
      }

      const updatedUser = await storage.deactivateUser(req.params.id);
      res.json(updatedUser);
    } catch (error) {
      console.error("Error deactivating user:", error);
      res.status(500).json({ message: "Failed to deactivate user" });
    }
  });

  app.patch('/api/users/:id/reactivate', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (user?.role !== 'Admin') {
        return res.status(403).json({ message: "Admin access required" });
      }

      const updatedUser = await storage.reactivateUser(req.params.id);
      res.json(updatedUser);
    } catch (error) {
      console.error("Error reactivating user:", error);
      res.status(500).json({ message: "Failed to reactivate user" });
    }
  });

  // Lead routes
  app.get('/api/leads', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);

      if (!user) {
        return res.status(401).json({ message: "User not found" });
      }

      // Property Manager role should not have access to leads
      if (user.role === "Property Manager") {
        return res.status(403).json({ message: "Access denied" });
      }

      const allLeads = await storage.getLeads();

      // Sales Agents and Marketing Executives only see leads assigned to them
      if (user.role === "Sales Agent" || user.role === "Marketing Executive") {
        const filteredLeads = allLeads.filter(lead => lead.assignedTo === userId);
        return res.json(filteredLeads);
      }

      // Only Admin sees all leads
      res.json(allLeads);
    } catch (error) {
      console.error("Error fetching leads:", error);
      res.status(500).json({ message: "Failed to fetch leads" });
    }
  });

  app.get('/api/leads/:id', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);

      if (!user) {
        return res.status(401).json({ message: "User not found" });
      }

      // Property Manager role should not have access to leads
      if (user.role === "Property Manager") {
        return res.status(403).json({ message: "Access denied" });
      }

      const lead = await storage.getLead(req.params.id);
      if (!lead) {
        return res.status(404).json({ message: "Lead not found" });
      }

      // Sales Agents and Marketing Executives can only access leads assigned to them
      if ((user.role === "Sales Agent" || user.role === "Marketing Executive") && lead.assignedTo !== userId) {
        return res.status(403).json({ message: "Access denied" });
      }

      res.json(lead);
    } catch (error) {
      console.error("Error fetching lead:", error);
      res.status(500).json({ message: "Failed to fetch lead" });
    }
  });

  app.post('/api/leads', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);

      if (!user) {
        return res.status(401).json({ message: "User not found" });
      }

      // Property Manager role should not have access to leads
      if (user.role === "Property Manager") {
        return res.status(403).json({ message: "Access denied" });
      }

      const validated = insertLeadSchema.parse(req.body);

      // Validate that assigned user exists if assignedTo is provided
      if (validated.assignedTo) {
        const assignedUser = await storage.getUser(validated.assignedTo);
        if (!assignedUser) {
          return res.status(400).json({ message: "Assigned user does not exist. Please refresh the page and try again." });
        }
      }

      const lead = await storage.createLead(validated);
      res.status(201).json(lead);
    } catch (error) {
      console.error("Error creating lead:", error);
      res.status(400).json({ message: "Failed to create lead" });
    }
  });

  app.patch('/api/leads/:id', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);

      if (!user) {
        return res.status(401).json({ message: "User not found" });
      }

      // Property Manager role should not have access to leads
      if (user.role === "Property Manager") {
        return res.status(403).json({ message: "Access denied" });
      }

      const existingLead = await storage.getLead(req.params.id);
      if (!existingLead) {
        return res.status(404).json({ message: "Lead not found" });
      }

      // Sales Agents and Marketing Executives can only update leads assigned to them
      if ((user.role === "Sales Agent" || user.role === "Marketing Executive") && existingLead.assignedTo !== userId) {
        return res.status(403).json({ message: "Access denied" });
      }

      const validated = insertLeadSchema.partial().parse(req.body);

      // Validate that assigned user exists if assignedTo is being updated
      if (validated.assignedTo) {
        const assignedUser = await storage.getUser(validated.assignedTo);
        if (!assignedUser) {
          return res.status(400).json({ message: "Assigned user does not exist. Please refresh the page and try again." });
        }
      }

      const lead = await storage.updateLead(req.params.id, validated);
