  app.post('/api/documents', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const validated = insertDocumentAttachmentSchema.parse({
        ...req.body,
        uploadedBy: userId,
      });

      // Try to set ACL policy, but don't fail if file isn't immediately available
      let normalizedUrl = validated.fileUrl;
      try {
        normalizedUrl = await objectStorageService.trySetObjectEntityAclPolicy(
          validated.fileUrl,
          {
            owner: userId,
            visibility: "private",
          }
        );
      } catch (aclError) {
        // If file not found, it might not be available yet - proceed anyway
        console.warn("Could not set ACL policy immediately (file may not be available yet):", aclError);
        normalizedUrl = objectStorageService.normalizeObjectEntityPath(validated.fileUrl);
      }

      const document = await storage.createDocument({
        ...validated,
        fileUrl: normalizedUrl,
      });

      res.status(201).json(document);
    } catch (error) {
      console.error("Error creating document:", error);
      res.status(400).json({ message: "Failed to create document" });
    }
  });

  // View document (inline)
  app.get('/api/documents/:id/view', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const document = await storage.getDocument(req.params.id);

      if (!document) {
        return res.status(404).json({ message: "Document not found" });
      }

      // Retry logic for file availability (object storage eventual consistency)
      let objectFile;
      let retries = 3;
      let lastError;

      while (retries > 0) {
        try {
          objectFile = await objectStorageService.getObjectEntityFile(document.fileUrl);
          break; // Success, exit retry loop
        } catch (err) {
          lastError = err;
          retries--;
          if (retries > 0) {
            // Wait 500ms before retry
            await new Promise(resolve => setTimeout(resolve, 500));
          }
        }
      }

      if (!objectFile) {
        console.error("File not found after retries:", lastError);
        return res.status(404).json({ message: "File not yet available. Please try again in a moment." });
      }

      const canAccess = await objectStorageService.canAccessObjectEntity({
        userId,
        objectFile,
      });

      if (!canAccess) {
        return res.status(403).json({ message: "Access denied" });
      }

      // Get file metadata
      const [metadata] = await objectFile.getMetadata();

      // Set headers for inline viewing
      res.set({
        "Content-Type": document.mimeType || "application/pdf",
        "Content-Length": metadata.size,
        "Content-Disposition": `inline; filename="${document.fileName}"`,
        "Cache-Control": "private, max-age=3600",
      });

      // Stream the file
      const stream = objectFile.createReadStream();
      stream.on("error", (err) => {
        console.error("Stream error:", err);
        if (!res.headersSent) {
          res.status(500).json({ error: "Error streaming file" });
        }
      });
      stream.pipe(res);
    } catch (error) {
      console.error("Error viewing document:", error);
      if (!res.headersSent) {
        res.status(500).json({ message: "Failed to view document" });
      }
    }
  });

