import {
  users,
  leads,
  properties,
  owners,
  clients,
  activities,
  contactSubmissions,
  documentAttachments,
  type User,
  type UpsertUser,
  type Lead,
  type InsertLead,
  type Property,
  type InsertProperty,
  type Owner,
  type InsertOwner,
  type Client,
  type InsertClient,
  type Activity,
  type InsertActivity,
  type ContactSubmission,
  type InsertContactSubmission,
  type DocumentAttachment,
  type InsertDocumentAttachment,
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, and, gte, count, sql, inArray } from "drizzle-orm";

export interface IStorage {
  // User operations (required for Replit Auth)
  getUser(id: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;
  getUsersByRole(role: string): Promise<User[]>;
  getAllUsers(): Promise<User[]>;
  updateUserRole(id: string, role: string): Promise<User>;
  createManualUser(email: string, role: string): Promise<User>;
  deactivateUser(id: string): Promise<User>;
  reactivateUser(id: string): Promise<User>;

  // Lead operations
  getLeads(): Promise<Lead[]>;
  getLead(id: string): Promise<Lead | undefined>;
  createLead(lead: InsertLead): Promise<Lead>;
  updateLead(id: string, lead: Partial<InsertLead>): Promise<Lead>;
  deleteLead(id: string): Promise<void>;

  // Property operations
  getProperties(): Promise<Property[]>;
  getProperty(id: string): Promise<Property | undefined>;
  createProperty(property: InsertProperty): Promise<Property>;
  updateProperty(id: string, property: Partial<InsertProperty>): Promise<Property>;
  deleteProperty(id: string): Promise<void>;

  // Owner operations
  getOwners(): Promise<Owner[]>;
  getOwner(id: string): Promise<Owner | undefined>;
  createOwner(owner: InsertOwner): Promise<Owner>;
  updateOwner(id: string, owner: Partial<InsertOwner>): Promise<Owner>;
  deleteOwner(id: string): Promise<void>;

  // Client operations
  getClients(): Promise<Client[]>;
  getClient(id: string): Promise<Client | undefined>;
  createClient(client: InsertClient): Promise<Client>;
  updateClient(id: string, client: Partial<InsertClient>): Promise<Client>;
  deleteClient(id: string): Promise<void>;

  // Activity operations
  getActivities(leadId: string): Promise<Activity[]>;
  createActivity(activity: InsertActivity): Promise<Activity>;
  getRecentActivities(limit: number): Promise<Activity[]>;

  // Contact submission operations
  getContactSubmissions(): Promise<ContactSubmission[]>;
  createContactSubmission(submission: InsertContactSubmission): Promise<ContactSubmission>;

  // Dashboard stats
  getDashboardStats(): Promise<any>;
  getSalesData(): Promise<any[]>;
  getLeadSourceData(): Promise<any[]>;
  getTopAgents(): Promise<any[]>;
  getDailyExecutiveActivities(): Promise<any[]>;

  // Bulk import operations
  bulkImportLeads(leadsData: InsertLead[]): Promise<{ inserted: number; updated: number; errors: any[] }>;
  bulkImportProperties(propertiesData: InsertProperty[]): Promise<{ inserted: number; updated: number; errors: any[] }>;
  bulkImportOwners(ownersData: InsertOwner[]): Promise<{ inserted: number; updated: number; errors: any[] }>;
  bulkImportClients(clientsData: InsertClient[]): Promise<{ inserted: number; updated: number; errors: any[] }>;

  // Helper methods for import
  getOwnerByEmail(email: string): Promise<Owner | undefined>;
  getLeadByEmail(email: string): Promise<Lead | undefined>;
  getPropertyByTitleAndLocation(title: string, location: string): Promise<Property | undefined>;

  // Document attachment operations
  getDocuments(entityType?: string, entityId?: string): Promise<DocumentAttachment[]>;
  getDocument(id: string): Promise<DocumentAttachment | undefined>;
  createDocument(document: InsertDocumentAttachment): Promise<DocumentAttachment>;
  updateDocument(id: string, document: Partial<InsertDocumentAttachment>): Promise<DocumentAttachment>;
  deleteDocument(id: string): Promise<void>;
}

export class DatabaseStorage implements IStorage {
  // User operations
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    // Check if user exists by email first
    const [existingUser] = await db
      .select()
      .from(users)
      .where(eq(users.email, userData.email!));

    if (existingUser) {
      // If user was pre-created and this is their first login, update their ID
      // and migrate all related records

      // If ID is different, this is a pre-created user logging in for first time
      // Use a transaction to ensure atomicity
      if (existingUser.id !== userData.id) {
        return await db.transaction(async (tx) => {
          // Get all records that need migration
          const leadsToMigrate = await tx
            .select()
            .from(leads)
            .where(eq(leads.assignedTo, existingUser.id));

          const activitiesToMigrate = await tx
            .select()
            .from(activities)
            .where(eq(activities.performedBy, existingUser.id));

          const documentsToMigrate = await tx
            .select()
            .from(documentAttachments)
            .where(eq(documentAttachments.uploadedBy, existingUser.id));

          const leadIds = leadsToMigrate.map(l => l.id);
          const activityIds = activitiesToMigrate.map(a => a.id);
          const documentIds = documentsToMigrate.map(d => d.id);

          // Step 1: Temporarily set foreign keys to NULL
          if (leadIds.length > 0) {
            await tx
              .update(leads)
              .set({ assignedTo: null })
              .where(inArray(leads.id, leadIds));
          }

          if (activityIds.length > 0) {
            await tx
              .update(activities)
              .set({ performedBy: null })
              .where(inArray(activities.id, activityIds));
          }

          if (documentIds.length > 0) {
            await tx
              .update(documentAttachments)
              .set({ uploadedBy: null })
              .where(inArray(documentAttachments.id, documentIds));
          }

          // Step 2: Update the user's ID and profile in one go
          const [updatedUser] = await tx
            .update(users)
            .set({
              id: userData.id,
              firstName: userData.firstName,
              lastName: userData.lastName,
              profileImageUrl: userData.profileImageUrl,
              updatedAt: new Date(),
            })
            .where(eq(users.email, userData.email!))
            .returning();

          // Step 3: Restore all foreign key references with the new user ID
          if (leadIds.length > 0) {
            await tx
              .update(leads)
              .set({ assignedTo: userData.id })
              .where(inArray(leads.id, leadIds));
          }

          if (activityIds.length > 0) {
            await tx
              .update(activities)
              .set({ performedBy: userData.id })
              .where(inArray(activities.id, activityIds));
          }

          if (documentIds.length > 0) {
            await tx
              .update(documentAttachments)
              .set({ uploadedBy: userData.id })
              .where(inArray(documentAttachments.id, documentIds));
          }

          return updatedUser;
        });
      }

      // Update profile information only
      const [updatedUser] = await db
        .update(users)
        .set({
          firstName: userData.firstName,
          lastName: userData.lastName,
          profileImageUrl: userData.profileImageUrl,
          updatedAt: new Date(),
        })
        .where(eq(users.email, userData.email!))
        .returning();
      return updatedUser;
    }

    // Check if this is the first user in the system
    const userCount = await db.select({ count: sql`count(*)::int` }).from(users);
    const isFirstUser = userCount[0]?.count === 0;

    // Auto-assign Admin role to first user (for bootstrap)
    // Optional: Check against trusted admin email pattern from env
    const adminPattern = process.env.ADMIN_EMAIL_PATTERN || 'admin';
    const isTrustedEmail = userData.email?.toLowerCase().includes(adminPattern.toLowerCase());
    const shouldBeAdmin = isFirstUser && isTrustedEmail;

    // Insert new user if doesn't exist
    const [user] = await db
      .insert(users)
      .values({
        ...userData,
        role: shouldBeAdmin ? 'Admin' : userData.role || 'Sales Agent',
      })
      .onConflictDoUpdate({
        target: users.id,
        set: {
          email: userData.email,
          firstName: userData.firstName,
          lastName: userData.lastName,
          profileImageUrl: userData.profileImageUrl,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  async getUsersByRole(role: string): Promise<User[]> {
    return await db.select().from(users).where(and(eq(users.role, role), eq(users.isActive, 1)));
  }

  async getAllUsers(): Promise<User[]> {
    return await db.select().from(users).orderBy(desc(users.createdAt));
  }

  async updateUserRole(id: string, role: string): Promise<User> {
    const [updatedUser] = await db
      .update(users)
      .set({ role, updatedAt: new Date() })
      .where(eq(users.id, id))
      .returning();
    return updatedUser;
  }

  async createManualUser(email: string, role: string): Promise<User> {
    // Create user with placeholder values - will be updated on first login
    const [user] = await db
      .insert(users)
      .values({
        email,
        role,
        firstName: email.split('@')[0], // Use email prefix as placeholder
        lastName: '',
      })
      .returning();
    return user;
  }

  async deactivateUser(id: string): Promise<User> {
    const [user] = await db
      .update(users)
      .set({ isActive: 0, updatedAt: new Date() })
      .where(eq(users.id, id))
      .returning();
    return user;
  }

  async reactivateUser(id: string): Promise<User> {
    const [user] = await db
      .update(users)
      .set({ isActive: 1, updatedAt: new Date() })
      .where(eq(users.id, id))
      .returning();
    return user;
  }

  // Lead operations
  async getLeads(): Promise<Lead[]> {
    return await db.select().from(leads).orderBy(desc(leads.createdAt));
  }

  async getLead(id: string): Promise<Lead | undefined> {
    const [lead] = await db.select().from(leads).where(eq(leads.id, id));
    return lead;
  }

  async createLead(leadData: InsertLead): Promise<Lead> {
    const [lead] = await db.insert(leads).values(leadData).returning();
    return lead;
  }

  async updateLead(id: string, leadData: Partial<InsertLead>): Promise<Lead> {
    const [lead] = await db
      .update(leads)
      .set({ ...leadData, updatedAt: new Date() })
      .where(eq(leads.id, id))
      .returning();
    return lead;
  }

  async deleteLead(id: string): Promise<void> {
    await db.delete(leads).where(eq(leads.id, id));
  }

  // Property operations
  async getProperties(): Promise<any[]> {
    const result = await db
      .select({
        id: properties.id,
        title: properties.title,
        location: properties.location,
        latitude: properties.latitude,
        longitude: properties.longitude,
        price: properties.price,
        area: properties.area,
        type: properties.type,
        transactionType: properties.transactionType,
        status: properties.status,
        ownerId: properties.ownerId,
        images: properties.images,
        description: properties.description,
        createdAt: properties.createdAt,
        updatedAt: properties.updatedAt,
        ownerName: owners.name,
        ownerPhone: owners.phone,
        ownerEmail: owners.email,
      })
      .from(properties)
      .leftJoin(owners, eq(properties.ownerId, owners.id))
      .orderBy(desc(properties.createdAt));
    return result;
  }

  async getProperty(id: string): Promise<any | undefined> {
    const [property] = await db
      .select({
        id: properties.id,
        title: properties.title,
        location: properties.location,
        latitude: properties.latitude,
        longitude: properties.longitude,
        price: properties.price,
        area: properties.area,
        type: properties.type,
        transactionType: properties.transactionType,
        status: properties.status,
        ownerId: properties.ownerId,
        images: properties.images,
        description: properties.description,
        createdAt: properties.createdAt,
        updatedAt: properties.updatedAt,
        ownerName: owners.name,
        ownerPhone: owners.phone,
        ownerEmail: owners.email,
      })
      .from(properties)
      .leftJoin(owners, eq(properties.ownerId, owners.id))
      .where(eq(properties.id, id));
    return property;
  }

  async createProperty(propertyData: InsertProperty): Promise<Property> {
    const [property] = await db.insert(properties).values(propertyData).returning();
    return property;
  }

  async updateProperty(id: string, propertyData: Partial<InsertProperty>): Promise<Property> {
    const [property] = await db
      .update(properties)
      .set({ ...propertyData, updatedAt: new Date() })
      .where(eq(properties.id, id))
      .returning();
    return property;
  }

  async deleteProperty(id: string): Promise<void> {
    await db.delete(properties).where(eq(properties.id, id));
  }

  // Owner operations
  async getOwners(): Promise<Owner[]> {
    return await db.select().from(owners).orderBy(desc(owners.createdAt));
  }

  async getOwner(id: string): Promise<Owner | undefined> {
    const [owner] = await db.select().from(owners).where(eq(owners.id, id));
    return owner;
  }

  async getOwnerByPhone(phone: string): Promise<Owner | undefined> {
    const [owner] = await db.select().from(owners).where(eq(owners.phone, phone));
    return owner;
  }

  async createOwner(ownerData: InsertOwner): Promise<Owner> {
    const [owner] = await db.insert(owners).values(ownerData).returning();
    return owner;
  }

  async updateOwner(id: string, ownerData: Partial<InsertOwner>): Promise<Owner> {
    const [owner] = await db
      .update(owners)
      .set({ ...ownerData, updatedAt: new Date() })
      .where(eq(owners.id, id))
      .returning();
    return owner;
  }

  async deleteOwner(id: string): Promise<void> {
    await db.delete(owners).where(eq(owners.id, id));
  }

  // Client operations
  async getClients(): Promise<Client[]> {
    return await db.select().from(clients).orderBy(desc(clients.createdAt));
  }

  async getClient(id: string): Promise<Client | undefined> {
    const [client] = await db.select().from(clients).where(eq(clients.id, id));
    return client;
  }

  async createClient(clientData: InsertClient): Promise<Client> {
    const [client] = await db.insert(clients).values(clientData).returning();
    return client;
  }

  async updateClient(id: string, clientData: Partial<InsertClient>): Promise<Client> {
    const [client] = await db
      .update(clients)
      .set({ ...clientData, updatedAt: new Date() })
      .where(eq(clients.id, id))
      .returning();
    return client;
  }

  async deleteClient(id: string): Promise<void> {
    await db.delete(clients).where(eq(clients.id, id));
  }

  // Activity operations
  async getActivities(leadId: string): Promise<Activity[]> {
    return await db
      .select()
      .from(activities)
      .where(eq(activities.leadId, leadId))
      .orderBy(desc(activities.createdAt));
  }

  async createActivity(activityData: InsertActivity): Promise<Activity> {
    const [activity] = await db.insert(activities).values(activityData).returning();
    return activity;
  }

  async getRecentActivities(limit: number = 10): Promise<Activity[]> {
    return await db
      .select()
      .from(activities)
      .orderBy(desc(activities.createdAt))
      .limit(limit);
  }

  // Contact submission operations
  async getContactSubmissions(): Promise<ContactSubmission[]> {
    return await db.select().from(contactSubmissions).orderBy(desc(contactSubmissions.createdAt));
  }

  async createContactSubmission(submissionData: InsertContactSubmission): Promise<ContactSubmission> {
    const [submission] = await db.insert(contactSubmissions).values(submissionData).returning();
    return submission;
  }

  // Dashboard stats
  async getDashboardStats(): Promise<any> {
    const [totalLeadsResult] = await db.select({ count: count() }).from(leads);
    const [activeLeadsResult] = await db
      .select({ count: count() })
      .from(leads)
      .where(sql`${leads.stage} != 'Closed'`);
    const [closedDealsResult] = await db
      .select({ count: count() })
      .from(leads)
      .where(eq(leads.stage, "Closed"));
    const [totalPropertiesResult] = await db.select({ count: count() }).from(properties);
    const [availablePropertiesResult] = await db
      .select({ count: count() })
      .from(properties)
      .where(eq(properties.status, "Available"));

    const soldProperties = await db
      .select({ price: properties.price })
      .from(properties)
      .where(eq(properties.status, "Sold"));

    const totalRevenue = soldProperties.reduce((sum, prop) => sum + Number(prop.price), 0);

    return {
      totalLeads: totalLeadsResult.count,
      activeLeads: activeLeadsResult.count,
      closedDeals: closedDealsResult.count,
      totalProperties: totalPropertiesResult.count,
      availableProperties: availablePropertiesResult.count,
      totalRevenue,
    };
  }

  async getSalesData(): Promise<any[]> {
    // Get actual sales revenue from sold properties grouped by year-month
    const soldProperties = await db
      .select({
        createdAt: properties.createdAt,
        price: properties.price,
      })
      .from(properties)
      .where(eq(properties.status, "Sold"));

    // Group by year-month and sum revenue
    const monthlyData: { [key: string]: number } = {};
    const monthNames = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];

    soldProperties.forEach(prop => {
      const date = new Date(prop.createdAt!);
      const yearMonth = `${date.getFullYear()}-${String(date.getMonth() + 1).padStart(2, '0')}`;
      monthlyData[yearMonth] = (monthlyData[yearMonth] || 0) + Number(prop.price);
    });

    // Get last 6 months with proper year-month keys
    const currentDate = new Date();
    const last6Months = [];
    for (let i = 5; i >= 0; i--) {
      const date = new Date(currentDate.getFullYear(), currentDate.getMonth() - i, 1);
      const yearMonth = `${date.getFullYear()}-${String(date.getMonth() + 1).padStart(2, '0')}`;
      const monthName = monthNames[date.getMonth()];
      last6Months.push({
        month: monthName,
        sales: monthlyData[yearMonth] || 0,
      });
    }

    return last6Months;
  }

  async getLeadSourceData(): Promise<any[]> {
    const sourceData = await db
      .select({ source: leads.source, count: count() })
      .from(leads)
      .groupBy(leads.source);

    return sourceData.map(item => ({
      name: item.source,
      value: item.count,
    }));
  }

  async getTopAgents(): Promise<any[]> {
    const agentDeals = await db
      .select({
        agentId: leads.assignedTo,
        deals: count(),
      })
      .from(leads)
      .where(and(
        eq(leads.stage, "Closed"),
        sql`${leads.assignedTo} IS NOT NULL`
      ))
      .groupBy(leads.assignedTo)
      .orderBy(desc(count()))
      .limit(5);

    const agentsWithNames = await Promise.all(
      agentDeals.map(async (agent) => {
        const user = await this.getUser(agent.agentId!);
        return {
          id: agent.agentId,
          name: user?.firstName && user?.lastName 
            ? `${user.firstName} ${user.lastName}` 
            : user?.email || 'Unknown',
          deals: agent.deals,
        };
      })
    );

    return agentsWithNames;
  }

  async getDailyExecutiveActivities(): Promise<any[]> {
    // Get today's date at midnight
    const today = new Date();
    today.setHours(0, 0, 0, 0);

    // Get all users to filter by role and get names (only active users)
    const allUsers = await this.getAllUsers();
    const executives = allUsers.filter(u => 
      (u.role === "Sales Agent" || u.role === "Marketing Executive") && u.isActive
    );

    // Get data for each executive
    const executiveReports = await Promise.all(
      executives.map(async (user) => {
        // Count leads assigned today
        const leadsAssignedToday = await db
          .select({ count: count() })
          .from(leads)
          .where(and(
            eq(leads.assignedTo, user.id),
            gte(leads.createdAt, today)
          ));

        // Count site visits today (from activities)
        const siteVisitsToday = await db
          .select({ count: count() })
          .from(activities)
          .where(and(
            eq(activities.performedBy, user.id),
            eq(activities.type, 'site_visit'),
            gte(activities.createdAt, today)
          ));

        // Count leads closed today
        const leadsClosedToday = await db
          .select({ count: count() })
          .from(leads)
          .where(and(
            eq(leads.assignedTo, user.id),
            eq(leads.stage, 'Closed'),
            gte(leads.updatedAt, today)
          ));

        return {
          userId: user.id,
          name: `${user.firstName || ''} ${user.lastName || ''}`.trim() || user.email || 'Unknown',
          role: user.role,
          leadsAssigned: leadsAssignedToday[0]?.count || 0,
          siteVisits: siteVisitsToday[0]?.count || 0,
          leadsClosed: leadsClosedToday[0]?.count || 0,
        };
      })
    );

    // Sort by total activity (leads assigned + site visits + leads closed)
    return executiveReports.sort((a, b) => {
      const totalA = a.leadsAssigned + a.siteVisits + a.leadsClosed;
      const totalB = b.leadsAssigned + b.siteVisits + b.leadsClosed;
      return totalB - totalA;
    });
  }

  // Helper methods for import
  async getOwnerByEmail(email: string): Promise<Owner | undefined> {
    const [owner] = await db.select().from(owners).where(eq(owners.email, email));
    return owner;
  }

  async getLeadByEmail(email: string): Promise<Lead | undefined> {
    const [lead] = await db.select().from(leads).where(eq(leads.email, email));
    return lead;
  }

  async getPropertyByTitleAndLocation(title: string, location: string): Promise<Property | undefined> {
    const [property] = await db
      .select()
      .from(properties)
      .where(and(eq(properties.title, title), eq(properties.location, location)));
    return property;
  }

  // Bulk import operations
  async bulkImportLeads(leadsData: InsertLead[]): Promise<{ inserted: number; updated: number; errors: any[] }> {
    const errors: any[] = [];

    // Validate all rows first
    for (let i = 0; i < leadsData.length; i++) {
      const leadData = leadsData[i];

      if (!leadData.name || !leadData.phone || !leadData.source) {
        errors.push({ row: i + 1, error: "Missing required fields: name, phone, or source" });
      }

      // For duplicate detection, email is required
      if (!leadData.email) {
        errors.push({ row: i + 1, error: "Email is required for duplicate detection" });
      }
    }

    // If any validation errors, return early without importing
    if (errors.length > 0) {
      return { inserted: 0, updated: 0, errors };
    }

    let inserted = 0;
    let updated = 0;

    // All validations passed - import atomically within transaction
    await db.transaction(async (tx) => {
      for (const leadData of leadsData) {
        const [existingLead] = await tx
          .select()
          .from(leads)
          .where(eq(leads.email, leadData.email!));

        if (existingLead) {
          await tx.update(leads).set({ ...leadData, updatedAt: new Date() }).where(eq(leads.id, existingLead.id));
          updated++;
        } else {
          await tx.insert(leads).values(leadData);
          inserted++;
        }
      }
    });

    return { inserted, updated, errors };
  }

  async bulkImportProperties(propertiesData: any[]): Promise<{ inserted: number; updated: number; errors: any[] }> {
    const errors: any[] = [];

    // Helper function to clean numeric values
    const cleanNumericValue = (value: any): number | null => {
      if (value === null || value === undefined || value === '') return null;

      // Convert to string and remove non-numeric characters except decimal point
      const cleanedStr = String(value).replace(/[^\d.]/g, '');
      const num = parseFloat(cleanedStr);

      return isNaN(num) ? null : num;
    };

    // Validate all rows first
    for (let i = 0; i < propertiesData.length; i++) {
      const propertyData = propertiesData[i];

      // If owner data provided, validate it
      if (propertyData.ownerName || propertyData.ownerPhone) {
        if (!propertyData.ownerName || !propertyData.ownerPhone) {
          errors.push({ row: i + 1, error: "Both owner name and phone are required if owner data is provided" });
        }
      }
    }

    // If any validation errors, return early without importing
    if (errors.length > 0) {
      return { inserted: 0, updated: 0, errors };
    }

    let inserted = 0;
    let updated = 0;

    // All validations passed - import atomically within transaction
    await db.transaction(async (tx) => {
      for (const propertyData of propertiesData) {
        let ownerId = propertyData.ownerId;

        // Handle automatic owner creation/linking
        if (propertyData.ownerName && propertyData.ownerPhone) {
          // Look for existing owner by phone
          const [existingOwner] = await tx
            .select()
            .from(owners)
            .where(eq(owners.phone, propertyData.ownerPhone));

          if (existingOwner) {
            ownerId = existingOwner.id;
          } else {
            // Create new owner
            const [newOwner] = await tx
              .insert(owners)
              .values({
                name: propertyData.ownerName,
                phone: propertyData.ownerPhone,
                email: null,
                address: null,
              })
              .returning();
            ownerId = newOwner.id;
          }
        }

        // Prepare property data without owner fields and clean numeric values
        const { ownerName, ownerPhone, ...cleanPropertyData } = propertyData;

        // Clean numeric fields (price and area)
        if (cleanPropertyData.price !== undefined) {
          cleanPropertyData.price = cleanNumericValue(cleanPropertyData.price);
        }
        if (cleanPropertyData.area !== undefined) {
          cleanPropertyData.area = cleanNumericValue(cleanPropertyData.area);
        }
        if (cleanPropertyData.latitude !== undefined) {
          cleanPropertyData.latitude = cleanNumericValue(cleanPropertyData.latitude);
        }
        if (cleanPropertyData.longitude !== undefined) {
          cleanPropertyData.longitude = cleanNumericValue(cleanPropertyData.longitude);
        }

        const finalPropertyData = { ...cleanPropertyData, ownerId };

        // Check for existing property
        const [existingProperty] = await tx
          .select()
          .from(properties)
          .where(and(eq(properties.title, finalPropertyData.title), eq(properties.location, finalPropertyData.location)));

        if (existingProperty) {
          await tx.update(properties).set({ ...finalPropertyData, updatedAt: new Date() }).where(eq(properties.id, existingProperty.id));
          updated++;
        } else {
          await tx.insert(properties).values(finalPropertyData);
          inserted++;
        }
      }
    });

    return { inserted, updated, errors };
  }

  async bulkImportOwners(ownersData: InsertOwner[]): Promise<{ inserted: number; updated: number; errors: any[] }> {
    const errors: any[] = [];

    // Validate all rows first
    for (let i = 0; i < ownersData.length; i++) {
      const ownerData = ownersData[i];

      if (!ownerData.name || !ownerData.phone) {
        errors.push({ row: i + 1, error: "Missing required fields: name or phone" });
      }

      // For duplicate detection, email is required
      if (!ownerData.email) {
        errors.push({ row: i + 1, error: "Email is required for duplicate detection" });
      }
    }

    // If any validation errors, return early without importing
    if (errors.length > 0) {
      return { inserted: 0, updated: 0, errors };
    }

    let inserted = 0;
    let updated = 0;

    // All validations passed - import atomically within transaction
    await db.transaction(async (tx) => {
      for (const ownerData of ownersData) {
        const [existingOwner] = await tx
          .select()
          .from(owners)
          .where(eq(owners.email, ownerData.email!));

        if (existingOwner) {
          await tx.update(owners).set({ ...ownerData, updatedAt: new Date() }).where(eq(owners.id, existingOwner.id));
          updated++;
        } else {
          await tx.insert(owners).values(ownerData);
          inserted++;
        }
      }
    });

    return { inserted, updated, errors };
  }

  async bulkImportClients(clientsData: InsertClient[]): Promise<{ inserted: number; updated: number; errors: any[] }> {
    const errors: any[] = [];

    // Validate all rows first
    for (let i = 0; i < clientsData.length; i++) {
      const clientData = clientsData[i];

      if (!clientData.name || !clientData.phone) {
        errors.push({ row: i + 1, error: "Missing required fields: name or phone" });
      }

      // For duplicate detection, prefer email but use phone as fallback
      if (!clientData.email && !clientData.phone) {
        errors.push({ row: i + 1, error: "Either email or phone is required for duplicate detection" });
      }
    }

    // If any validation errors, return early without importing
    if (errors.length > 0) {
      return { inserted: 0, updated: 0, errors };
    }

    let inserted = 0;
    let updated = 0;

    // All validations passed - import atomically within transaction
    await db.transaction(async (tx) => {
      for (const clientData of clientsData) {
        // Try to find by email first, then by phone
        let existingClient;
        if (clientData.email) {
          [existingClient] = await tx
            .select()
            .from(clients)
            .where(eq(clients.email, clientData.email));
        }
        if (!existingClient && clientData.phone) {
          [existingClient] = await tx
            .select()
            .from(clients)
            .where(eq(clients.phone, clientData.phone));
        }

        if (existingClient) {
          await tx.update(clients).set({ ...clientData, updatedAt: new Date() }).where(eq(clients.id, existingClient.id));
          updated++;
        } else {
          await tx.insert(clients).values(clientData);
          inserted++;
        }
      }
    });

    return { inserted, updated, errors };
  }

  // Document attachment operations
  async getDocuments(entityType?: string, entityId?: string): Promise<DocumentAttachment[]> {
    let query = db.select().from(documentAttachments);

    if (entityType && entityId) {
      return await query
        .where(and(
          eq(documentAttachments.entityType, entityType),
          eq(documentAttachments.entityId, entityId)
        ))
        .orderBy(desc(documentAttachments.createdAt));
    } else if (entityType) {
      return await query
        .where(eq(documentAttachments.entityType, entityType))
        .orderBy(desc(documentAttachments.createdAt));
    }

    return await query.orderBy(desc(documentAttachments.createdAt));
  }

  async getDocument(id: string): Promise<DocumentAttachment | undefined> {
    const [document] = await db
      .select()
      .from(documentAttachments)
      .where(eq(documentAttachments.id, id));
    return document;
  }

  async createDocument(documentData: InsertDocumentAttachment): Promise<DocumentAttachment> {
    const [document] = await db
      .insert(documentAttachments)
      .values(documentData)
      .returning();
    return document;
  }

  async updateDocument(id: string, documentData: Partial<InsertDocumentAttachment>): Promise<DocumentAttachment> {
    const [document] = await db
      .update(documentAttachments)
      .set(documentData)
      .where(eq(documentAttachments.id, id))
      .returning();
    return document;
  }

  async deleteDocument(id: string): Promise<void> {
    await db.delete(documentAttachments).where(eq(documentAttachments.id, id));
  }
}

export const storage = new DatabaseStorage();

