import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Upload, Download, FileSpreadsheet, CheckCircle2, XCircle, AlertCircle, FileText, Trash2 } from "lucide-react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Badge } from "@/components/ui/badge";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import * as XLSX from "xlsx";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

type EntityType = "leads" | "properties" | "owners" | "clients";

interface ImportResult {
  inserted: number;
  updated: number;
  errors: Array<{ row: number; error: string }>;
}

export default function ImportPage() {
  const { toast } = useToast();
  const [selectedEntity, setSelectedEntity] = useState<EntityType>("leads");
  const [file, setFile] = useState<File | null>(null);
  const [parsedData, setParsedData] = useState<any[]>([]);
  const [importResult, setImportResult] = useState<ImportResult | null>(null);
  const [isImporting, setIsImporting] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const rowsPerPage = 10;

  const { data: user } = useQuery<any>({
    queryKey: ["/api/auth/user"],
  });

  // Check if user is admin
  const isAdmin = user?.role === "Admin";

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const selectedFile = e.target.files?.[0];
    if (selectedFile) {
      setFile(selectedFile);
      parseExcel(selectedFile);
    }
  };

  const parseExcel = (file: File) => {
    const reader = new FileReader();
    reader.onload = (e) => {
      const data = e.target?.result;
      const workbook = XLSX.read(data, { type: "binary" });
      const sheetName = workbook.SheetNames[0];
      const worksheet = workbook.Sheets[sheetName];
      const jsonData = XLSX.utils.sheet_to_json(worksheet);
      setParsedData(jsonData);
      setImportResult(null);
      setCurrentPage(1); // Reset to first page on new upload
    };
    reader.readAsBinaryString(file);
  };

  const mapDataToEntity = (data: any[]): any[] => {
    if (selectedEntity === "leads") {
      return data.map((row: any) => ({
        name: row.Name || row.name || "",
        phone: row.Phone || row.phone || "",
        email: row.Email || row.email || null,
        source: row.Source || row.source || "Website",
        budget: row.Budget || row.budget ? String(row.Budget || row.budget) : null,
        preferredLocation: row["Preferred Location"] || row.preferredLocation || null,
        stage: row.Stage || row.stage || "New",
        assignedTo: row["Assigned To"] || row.assignedTo || null,
      }));
    } else if (selectedEntity === "properties") {
      return data.map((row: any) => ({
        // Map user's fields to our schema
        title: row.PropertyName || row.propertyName || row.Title || row.title || "",
        type: row.PropertyTypeName || row.propertyTypeName || row.Type || row.type || "Residential",
        location: row.LocationName || row.locationName || row.CityName || row.cityName || row.Location || row.location || "",
        address: row.AddressName || row.addressName || row.BuildingName || row.buildingName || null,
        price: (row.ExpectedPrice || row.expectedPrice || row.Price || row.price) ? String(row.ExpectedPrice || row.expectedPrice || row.Price || row.price) : "",
        area: (row.SuperAreaName || row.superAreaName || row.BuiltAreaName || row.builtAreaName || row.CarpetAreaName || row.carpetAreaName || row.Area || row.area) ? Number(row.SuperAreaName || row.superAreaName || row.BuiltAreaName || row.builtAreaName || row.CarpetAreaName || row.carpetAreaName || row.Area || row.area) : 0,
        status: row.Status || row.status || "Available",
        description: row.Description || row.description || null,
        latitude: row.Latitude || row.latitude ? String(row.Latitude || row.latitude) : null,
        longitude: row.Longitude || row.longitude ? String(row.Longitude || row.longitude) : null,
        // Owner data for automatic creation/linking
        ownerName: row.CustomerFullName || row.customerFullName || "",
        ownerPhone: row.CustomerMobile || row.customerMobile || "",
      }));
    } else if (selectedEntity === "owners") {
      return data.map((row: any) => ({
        name: row.Name || row.name || "",
        phone: row.Phone || row.phone || "",
        email: row.Email || row.email || null,
        address: row.Address || row.address || null,
      }));
    } else if (selectedEntity === "clients") {
      return data.map((row: any) => ({
        name: row.FullName || row.fullName || row.Name || row.name || "",
        phone: row.Mobile || row.mobile || row.Phone || row.phone || "",
        email: row.Email || row.email || null,
        address: row.AddressName || row.addressName || row.Address || row.address || null,
        linkedLeadId: row["Linked Lead ID"] || row.linkedLeadId || null,
        linkedPropertyId: row["Linked Property ID"] || row.linkedPropertyId || null,
      }));
    }
    return [];
  };

  const handleImport = async () => {
    if (!parsedData.length) {
      toast({
        title: "No data to import",
        description: "Please upload a valid Excel file first.",
        variant: "destructive",
      });
      return;
    }

    setIsImporting(true);
    try {
      const mappedData = mapDataToEntity(parsedData);
      console.log("ðŸ“Š Starting import:", { entity: selectedEntity, totalRows: mappedData.length });
      console.log("ðŸ“‹ First mapped row sample:", mappedData[0]);

      // For large imports, process in batches (smaller batches for reliability)
      const BATCH_SIZE = 100;
      let totalInserted = 0;
      let totalUpdated = 0;
      let allErrors: any[] = [];

      for (let i = 0; i < mappedData.length; i += BATCH_SIZE) {
        const batch = mappedData.slice(i, i + BATCH_SIZE);
        const batchNum = Math.floor(i / BATCH_SIZE) + 1;
        const totalBatches = Math.ceil(mappedData.length / BATCH_SIZE);

        console.log(`ðŸ“¦ Processing batch ${batchNum}/${totalBatches}: rows ${i + 1}-${Math.min(i + BATCH_SIZE, mappedData.length)}`);

        toast({
          title: `Processing batch ${batchNum} of ${totalBatches}`,
          description: `Processing rows ${i + 1} to ${Math.min(i + BATCH_SIZE, mappedData.length)}...`,
        });

        const response = await apiRequest("POST", `/api/import/${selectedEntity}`, {
          data: batch,
        });
        const result = response as unknown as ImportResult;
        console.log(`âœ… Batch ${batchNum} result:`, result);

        totalInserted += result.inserted || 0;
        totalUpdated += result.updated || 0;
        allErrors = [...allErrors, ...(Array.isArray(result.errors) ? result.errors : [])];
      }

      const finalResult: ImportResult = {
        inserted: totalInserted,
        updated: totalUpdated,
        errors: allErrors,
      };

      setImportResult(finalResult);

      // Invalidate relevant queries
      if (selectedEntity === "leads") {
        await queryClient.invalidateQueries({ queryKey: ["/api/leads"] });
      } else if (selectedEntity === "properties") {
        await queryClient.invalidateQueries({ queryKey: ["/api/properties"] });
      } else if (selectedEntity === "owners") {
        await queryClient.invalidateQueries({ queryKey: ["/api/owners"] });
      } else if (selectedEntity === "clients") {
        await queryClient.invalidateQueries({ queryKey: ["/api/clients"] });
      }

      toast({
        title: "Import completed",
        description: `Successfully imported ${finalResult.inserted + finalResult.updated} records.`,
      });
    } catch (error: any) {
      console.error("Import error:", error);
      toast({
        title: "Import failed",
        description: error.message || "Failed to import data. Please check the console for details.",
        variant: "destructive",
      });
    } finally {
      setIsImporting(false);
    }
  };

  const downloadTemplate = () => {
    let templateData: any[] = [];

    if (selectedEntity === "leads") {
      templateData = [{
        Name: "John Doe (Required)",
        Phone: "9876543210 (Required)",
        Email: "john@example.com (Required)",
        Source: "Website (Required)",
        Budget: "5000000",
        "Preferred Location": "Sangli",
        Stage: "New",
        "Assigned To": null,
      }];
    } else if (selectedEntity === "properties") {
      templateData = [{
        PropertyTypeName: "Residential (Required)",
        PropertyName: "3BHK Apartment (Required)",
        AddressName: "Near Main Square",
        BuildingName: "Sunshine Apartments",
        LocationName: "Sangli Main Road (Required)",
        CityName: "Sangli",
        SuperAreaName: "1500 (Required)",
        BuiltAreaName: "1400",
        CarpetAreaName: "1300",
        ExpectedPrice: "7500000 (Required)",
        CustomerFullName: "Owner Name (Required)",
        CustomerMobile: "9876543210 (Required)",
      }];
    } else if (selectedEntity === "owners") {
      templateData = [{
        Name: "Property Owner (Required)",
        Phone: "9876543210 (Required)",
        Email: "owner@example.com (Required)",
        Address: "123 Main Street, Sangli",
      }];
    } else if (selectedEntity === "clients") {
      templateData = [{
        FullName: "Client Name (Required)",
        Mobile: "9876543210 (Required)",
        Email: "client@example.com (Required)",
        Phone: "0233-2345678",
        AddressName: "123 Main Street, Sangli",
      }];
    }

    const worksheet = XLSX.utils.json_to_sheet(templateData);
    const workbook = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(workbook, worksheet, selectedEntity);
    XLSX.writeFile(workbook, `${selectedEntity}_template.xlsx`);
  };

  if (!isAdmin) {
    return (
      <div className="h-full flex items-center justify-center">
        <Card className="w-full max-w-md">
          <CardHeader>
            <CardTitle>Access Denied</CardTitle>
            <CardDescription>
              Only administrators can access the import functionality.
            </CardDescription>
          </CardHeader>
        </Card>
      </div>
    );
  }

  return (
    <div className="container mx-auto p-6 space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold">Data Management</h1>
          <p className="text-muted-foreground">Import data and manage documents</p>
        </div>
      </div>

      <Tabs defaultValue="import" className="w-full">
        <TabsList data-testid="tabs-data-management">
          <TabsTrigger value="import" data-testid="tab-excel-import">
            <FileSpreadsheet className="w-4 h-4 mr-2" />
            Excel Import
          </TabsTrigger>
          <TabsTrigger value="documents" data-testid="tab-document-library">
            <FileText className="w-4 h-4 mr-2" />
            Document Library
          </TabsTrigger>
        </TabsList>

        <TabsContent value="import" className="space-y-6 mt-6">
      <Card>
        <CardHeader>
          <CardTitle>Upload Excel File</CardTitle>
          <CardDescription>
            Select the entity type and upload an Excel file to import data
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-6">
          <div className="flex gap-4 items-end">
            <div className="flex-1">
              <label className="text-sm font-medium mb-2 block">Entity Type</label>
              <Select value={selectedEntity} onValueChange={(value: EntityType) => setSelectedEntity(value)}>
                <SelectTrigger data-testid="select-entity-type">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="leads">Leads</SelectItem>
                  <SelectItem value="properties">Properties</SelectItem>
                  <SelectItem value="owners">Owners</SelectItem>
                  <SelectItem value="clients">Clients</SelectItem>
                </SelectContent>
              </Select>
            </div>
            <Button
              variant="outline"
              onClick={downloadTemplate}
              data-testid="button-download-template"
            >
              <Download className="w-4 h-4 mr-2" />
              Download Template
            </Button>
          </div>

          <div>
            <label className="text-sm font-medium mb-2 block">Excel File</label>
            <div className="border-2 border-dashed rounded-lg p-8 text-center">
              <input
                type="file"
                accept=".xlsx,.xls"
                onChange={handleFileChange}
                className="hidden"
                id="file-upload"
                data-testid="input-file-upload"
              />
              <label htmlFor="file-upload" className="cursor-pointer">
                <FileSpreadsheet className="w-12 h-12 mx-auto mb-4 text-muted-foreground" />
                <p className="text-sm text-muted-foreground mb-2">
                  {file ? file.name : "Click to upload or drag and drop"}
                </p>
                <p className="text-xs text-muted-foreground">
                  Excel files only (.xlsx, .xls)
                </p>
              </label>
            </div>
          </div>

          {parsedData.length > 0 && (
            <Alert>
              <AlertCircle className="h-4 w-4" />
              <AlertDescription>
                Parsed {parsedData.length} rows from the Excel file. Review the data below before importing.
              </AlertDescription>
            </Alert>
          )}

          {parsedData.length > 0 && (
            <div className="border rounded-lg overflow-hidden">
              <div className="overflow-x-auto max-h-96">
                <Table>
                  <TableHeader>
                    <TableRow>
                      <TableHead className="sticky left-0 bg-background z-10">Row</TableHead>
                      {Object.keys(parsedData[0]).slice(0, 8).map((key) => (
                        <TableHead key={key}>{key}</TableHead>
                      ))}
                      {Object.keys(parsedData[0]).length > 8 && (
                        <TableHead className="text-muted-foreground">...</TableHead>
                      )}
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {parsedData.slice((currentPage - 1) * rowsPerPage, currentPage * rowsPerPage).map((row, idx) => (
                      <TableRow key={idx}>
                        <TableCell className="sticky left-0 bg-background z-10">{(currentPage - 1) * rowsPerPage + idx + 1}</TableCell>
                        {Object.values(row).slice(0, 8).map((value: any, colIdx) => (
                          <TableCell key={colIdx}>{String(value || '').substring(0, 50)}</TableCell>
                        ))}
                        {Object.values(row).length > 8 && (
                          <TableCell className="text-muted-foreground">...</TableCell>
                        )}
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </div>
              <div className="p-3 text-sm bg-muted border-t">
                <div className="flex items-center justify-between flex-wrap gap-4">
                  <span className="text-muted-foreground">
                    Showing {(currentPage - 1) * rowsPerPage + 1} to {Math.min(currentPage * rowsPerPage, parsedData.length)} of {parsedData.length} rows
                    {Object.keys(parsedData[0]).length > 8 && ` â€¢ ${Object.keys(parsedData[0]).length} columns (showing first 8)`}
                  </span>
                  <div className="flex items-center gap-2">
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => setCurrentPage(1)}
                      disabled={currentPage === 1}
                      data-testid="button-first-page"
                    >
                      First
                    </Button>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => setCurrentPage(p => Math.max(1, p - 1))}
                      disabled={currentPage === 1}
                      data-testid="button-prev-page"
                    >
                      Previous
                    </Button>
                    <span className="text-sm">
                      Page {currentPage} of {Math.ceil(parsedData.length / rowsPerPage)}
                    </span>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => setCurrentPage(p => Math.min(Math.ceil(parsedData.length / rowsPerPage), p + 1))}
                      disabled={currentPage === Math.ceil(parsedData.length / rowsPerPage)}
                      data-testid="button-next-page"
                    >
                      Next
                    </Button>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => setCurrentPage(Math.ceil(parsedData.length / rowsPerPage))}
                      disabled={currentPage === Math.ceil(parsedData.length / rowsPerPage)}
                      data-testid="button-last-page"
                    >
                      Last
                    </Button>
                  </div>
                </div>
              </div>
            </div>
          )}

          <div className="flex gap-4">
            <Button
              onClick={handleImport}
              disabled={!parsedData.length || isImporting}
              className="flex-1"
              data-testid="button-import-data"
            >
              <Upload className="w-4 h-4 mr-2" />
              {isImporting ? "Importing..." : "Import Data"}
            </Button>
          </div>
        </CardContent>
      </Card>

      {importResult && (
        <Card>
          <CardHeader>
            <CardTitle>Import Results</CardTitle>
            <CardDescription>Summary of the import operation</CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="flex gap-4">
              <Badge variant="default" className="flex items-center gap-1">
                <CheckCircle2 className="w-4 h-4" />
