const typeColors: Record<string, string> = {
  "Residential": "bg-blue-100 text-blue-700 hover:bg-blue-100",
  "Commercial": "bg-purple-100 text-purple-700 hover:bg-purple-100"
};

export function PropertyDetailDrawer({ open, onOpenChange, property }: PropertyDetailDrawerProps) {
  const { data: owners } = useQuery<Owner[]>({
    queryKey: ["/api/owners"],
  });

  // Fetch documents for this property
  const { data: documents = [] } = useQuery<any[]>({
    queryKey: property?.id ? [`/api/documents?entityType=property&entityId=${property.id}`] : ["/api/documents"],
    enabled: !!property?.id,
  });

  const owner = owners?.find(o => o.id === property?.ownerId);

  const handleDownload = async (docId: string, fileName: string) => {
    try {
      const response = await fetch(`/api/documents/${docId}/download`);
      if (!response.ok) {
        throw new Error("Download failed");
      }

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.href = url;
      a.download = fileName || "document.pdf";
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);
    } catch (error) {
      console.error("Download error:", error);
    }
  };

  const handleDownloadBrochure = async () => {
    if (!property?.id) return;

    try {
      const response = await fetch(`/api/pdf/property/${property.id}`);
      if (!response.ok) {
        throw new Error("Download failed");
      }

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.href = url;
      a.download = `property-${property.id}.pdf`;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);
    } catch (error) {
      console.error("Download brochure error:", error);
    }
  };

  if (!property) return null;

  return (
    <Sheet open={open} onOpenChange={onOpenChange}>
      <SheetContent className="w-full sm:max-w-lg overflow-y-auto">
        <SheetHeader>
          <div className="flex items-center justify-between gap-4">
            <SheetTitle className="text-2xl flex-1">{property.title}</SheetTitle>
            <Button
              variant="outline"
              size="sm"
              onClick={handleDownloadBrochure}
              data-testid="button-download-brochure"
            >
              <FileDown className="w-4 h-4 mr-2" />
              Brochure
            </Button>
          </div>
        </SheetHeader>

        <div className="mt-6 space-y-6">
          {/* Property Image */}
          <div className="relative h-56 rounded-lg overflow-hidden bg-muted">
            <img 
              src={property.images && property.images.length > 0 ? property.images[0] : getDefaultPropertyImage(property.type)} 
              alt={property.title ?? 'Property Image'}
              className="w-full h-full object-cover"
            />
          </div>

          {/* Status and Type Badges */}
          <div className="flex gap-2">
            {property.status && <Badge className={statusColors[property.status]}>{property.status}</Badge>}
            {property.type && <Badge className={typeColors[property.type]}>{property.type}</Badge>}
          </div>

          {/* Property Details */}
          <div className="space-y-3">
            <h3 className="font-semibold text-sm text-muted-foreground uppercase">Property Details</h3>
            <div className="space-y-2">
              {property.price && (
                <div className="flex items-center gap-3">
                  <DollarSign className="w-4 h-4 text-muted-foreground" />
                  <span className="text-sm">â‚¹{parseFloat(property.price).toLocaleString('en-IN')}</span>
                </div>
              )}
              {property.area && (
                <div className="flex items-center gap-3">
                  <Home className="w-4 h-4 text-muted-foreground" />
                  <span className="text-sm">{property.area.toLocaleString()} sqft</span>
                </div>
              )}
              {property.location && (
                <div className="flex items-center gap-3">
                  <MapPin className="w-4 h-4 text-muted-foreground" />
                  <span className="text-sm">{property.location}</span>
                </div>
              )}
            </div>
          </div>

          <Separator />

          {/* Owner Information */}
          {owner && (
            <>
              <Separator />
              <div className="space-y-3">
                <h3 className="font-semibold text-sm text-muted-foreground uppercase">Owner Information</h3>
                <div className="space-y-2">
                  <div className="flex items-center gap-3">
                    <User className="w-4 h-4 text-muted-foreground" />
                    <span className="text-sm">{owner.name}</span>
                  </div>
                  {owner.phone && (
                    <div className="flex items-center gap-3">
                      <span className="text-sm text-muted-foreground">Phone:</span>
                      <span className="text-sm">{owner.phone}</span>
                    </div>
                  )}
                  {owner.email && (
                    <div className="flex items-center gap-3">
                      <span className="text-sm text-muted-foreground">Email:</span>
                      <span className="text-sm">{owner.email}</span>
                    </div>
                  )}
                </div>
              </div>
            </>
          )}

          {/* Description */}
          {property.description && (
            <>
              <Separator />
              <div className="space-y-3">
                <h3 className="font-semibold text-sm text-muted-foreground uppercase">Description</h3>
                <p className="text-sm text-muted-foreground whitespace-pre-wrap">{property.description}</p>
              </div>
            </>
          )}

          {/* Documents */}
          <Separator />
          <div className="space-y-3">
            <h3 className="font-semibold text-sm text-muted-foreground uppercase">Documents</h3>
            {documents.length === 0 ? (
              <div className="text-center py-6 text-muted-foreground text-sm">
                <FileText className="w-8 h-8 mx-auto mb-2 opacity-50" />
                <p>No documents attached</p>
                <p className="text-xs mt-1">Upload documents in the Document Library tab</p>
              </div>
            ) : (
              <div className="space-y-2">
                {documents.map((doc: any) => (
                  <div 
                    key={doc.id} 
                    className="flex items-center justify-between p-3 border rounded-lg hover-elevate"
                    data-testid={`document-${doc.id}`}
                  >
                    <div className="flex items-center gap-3 flex-1 min-w-0">
                      <FileText className="w-4 h-4 text-muted-foreground flex-shrink-0" />
                      <div className="min-w-0 flex-1">
                        <p className="text-sm font-medium truncate">{doc.title}</p>
                        {doc.description && (
                          <p className="text-xs text-muted-foreground truncate">{doc.description}</p>
                        )}
                      </div>
                    </div>
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => handleDownload(doc.id, doc.fileName)}
                      data-testid={`button-download-doc-${doc.id}`}
                    >
                      <Download className="w-4 h-4" />
                    </Button>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </SheetContent>
    </Sheet>
  );
}

